import React, { Component } from 'react';
import Web3 from 'web3';
import logo from '../logo.png';
import './App.css';
import EvContract from '../abis/EvContract.json'
import Navbar from './Navbar';
import Identicon from 'identicon.js';


class App extends Component {

  async componentWillMount() {
    await this.loadWeb3()
    await this.loadBlockchainData()
  }

  

  async loadWeb3() {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else {
      window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
    }
  }

  async loadBlockchainData(){
    const web3 = window.web3
    const accounts = await web3.eth.getAccounts()
    console.log(accounts)
    this.setState({account: accounts[0]})
    //Network id
    const networkId = await web3.eth.net.getId()
    const networkData = EvContract.networks[networkId]
    
    if(networkData){
      const evContract = web3.eth.Contract(EvContract.abi, networkData.address)
      this.setState({ evContract })
      console.log(EvContract)
      const cntA = await evContract.methods.cntA().call()
      this.setState({ cntA })
      //load posts
      for(var i = 0; i < cntA; i++){
        const car = await evContract.methods.cars(i).call()
        this.setState({
          cars: [...this.state.cars, car]
        })
      }
      console.log({ cars: this.state.cars })
    }else{
      window.alert('EvContract contract not deployed to detected network.')
    }
    //address
    
    //abi
  }

  constructor(props) {
    super(props)
    this.state = {
      account: '',
      evContract: null,
      cars: []
    }
  }

  render() {
    return (
      <div>
        <Navbar account={this.state.account} />
        <div className="container-fluid mt-5">
          <div className="row">
            <main role="main" className="col-lg-12 ml-auto mr-auto" style={{ maxWidth: '500px'}}>
              <div className="content mr-auto ml-auto">
                <a
                  href="http://www.dappuniversity.com/bootcamp"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  <img src={logo} className="App-logo" alt="logo" />
                </a>
                <h1>예지랑 수정이랑 블록체인이랑 ㅎ!!!!</h1>
                
                <a
                  className="App-link"
                  href="http://www.dappuniversity.com/bootcamp"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  LEARN BLOCKCHAIN <u><b>NOW! </b></u>
                </a>

                { this.state.cars.map((car,key) => {
                  return(
                    <div className="card mb-4" key={key}>
                      <div className="card-header">
                        <small className="text-muted">Post header</small>
                      </div>
                      <ul id="postList" className="list-group list-group-flush">
                        <li className="list-group-item">
                          <p>{car.name}</p>
                        </li>
                        <li key={key} className="list-group-item py-2">
                          <p>Post Footer</p>
                        </li>
                      </ul>
                    </div>
                  )
                })}


              </div>
            </main>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
