import React, { Component } from 'react';
import logo2 from '../logo2.png';
import web3 from './web3';
//id=account를 app.js에서 정의함.(현재 계정주소 저장)
//현재계정에서 토큰주소에 이더보내면 토큰 받도록 해보기...

class Token extends Component{
  constructor(props) {
    super(props);

    this.state = {
        manager: '',
        players: [],
        balance: '',
        value : '',
        message : ''
    };
        this.onSubmit = this.onSubmit.bind(this);
        this.onClick = this.onClick.bind(this);
    }
    onSubmit = async (evt) => {
      evt.preventDefault();

      const accounts = await web3.eth.getAccounts();
      console.log(accounts[0]);

      this.setState({ message : '트랜잭션 처리중...' })
      console.log(web3.utils.toWei(this.state.value, 'ether'));

     // let receiptObj = await lottery.methods.enter().send({
     //     from : accounts[0],
     //     value : web3.utils.toWei(this.state.value, 'ether')
     // });

     // console.log(receiptObj);

      this.setState({ message : '트랜잭션이 성공 되었습니다' });
  };
    render(){
      return(
        <div className="container-fluid mt-5">
        <div className="row">
          <main role="main" className="col-lg-12 d-flex text-center"> 
            <div className="content mr-auto ml-auto">
              <img src={logo2} className="App-logo2" alt='mypic' />

              
              <h2>전력거래에 EVT 토큰을 사용하세요!</h2>
              <br></br>
              <h3>STEP1.</h3>
              <h4>계정에 EVT 토큰을 추가하세요.</h4>
              <h5>(EVT 토큰 : <b>0xC48Be0e8293fe6aDd2E52550b411e1186Cc11a83</b>) </h5>
              <h3>STEP2.</h3>
              <h4>ETH를 EVT로 환전하세요.</h4>
              <h5> (1 EVT는  0.1 ETH 입니다.)</h5>

              <input type="text" size="50" id="ethamount" placeholder="환전할 이더를 입력하세요. ex) 0.1" ></input>
              <br></br>
              <input type="text" size="50" id="toaddress" placeholder="" value = "0x80980817b58Eff1A55D8A5F95EbbBc880F325a5B" />
              <br></br>
              <form onSubmit={this.onSubmit}>
                    <h4>로또 참가하기</h4>
                    <div>
                        <label>금액 입력</label>
                        <input
                            value = {this.state.value}
                            onChange={evt => this.setState({ value : evt.target.value })}
                        />
                    </div>
                    <button>입력</button>
              </form>
                         
              <p>
                <div><span id="sendethstatus"></span></div>
              </p>
              <br></br>
              
            </div>
          </main>
        </div>
        </div>

  
      );
    }
  }
  
  export default Token;
