import React, { Component } from 'react';
import Identicon from 'identicon.js';
import logo from '../logo.png';
import Token from './Token.js'



class Main extends Component{

    render(){
        return(
            <div className="container-fluid mt-5">
            <div className="row">
              <main role="main" className="col-lg-12 d-flex text-center"> 
                <div className="content mr-auto ml-auto">

                  <a
                    href="Main.js"
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                  <img src={logo} className="App-logo" alt="logo" />
                  </a>

                  <br></br>
                  <br></br>
                  <h1>전력 거래를 위해 정보를 등록하세요</h1>
                  
                  
                  <br></br>
                  <br></br>
                  <br></br>


                  
                  <form onSubmit={(event) => {
                      event.preventDefault()
                      const carNum = this.carInfo1.value
                      const name = this.carInfo2.value
                      const socI = this.carInfo3.value
                      const socR = this.carInfo4.value

                      this.props.setCar(carNum, name, socI, socR)
                      }}>
                      <div className="form-group mr-sm-w">
                          <input id="carInfo1" ref={(input) => {this.carInfo1 = input}}
                                type="text" className="form-control" placeholder="차 번호를 입력하세요" required />

                          <input id="carInfo2" ref={(input) => {this.carInfo2 = input}}
                                type="text" className="form-control" placeholder="이름을 입력하세요" required />

                          <input id="carInfo3" ref={(input) => {this.carInfo3 = input}}
                                type="text" className="form-control" placeholder="현재 전력 상태를 입력하세요" required />

                          <input id="carInfo4" ref={(input) => {this.carInfo4 = input}}
                                type="text" className="form-control" placeholder="원하는 전력 상태를 입력하세요" required />

                      </div>
                      <button type="submit" className="btn btn-primary btn-block">정보등록</button>
                  </form>
                  <br></br>
                  <br></br>
                  <br></br>

                  { this.props.cars.map((car,key) => {
                    return(
                      <div className="card mb-4" key={key}>
                        <div className="card-header">
                          <small className="text-muted">자동차 소유주 정보</small>
                        </div>
                        <ul id="postList" className="list-group list-group-flush">
                          <li className="list-group-item">
                            <p>등록 번호 : {car.id.toString()}</p>
                            <p>자동차 번호 : {car.carNum}</p>
                            <p>자동차 주인 이름 : {car.name}</p>
                            <p>자동차 주인 주소 : {car.owner}</p>
                            //<p>거래 가격 : {car.value}</p>

                          </li>
                          <li className="list-group-item py-2" key={key}>
                            <button
                            address={car.id}
                            value={car.price}
                            onClick={(event) => {
                             this.props.buycar(event.target.address, event.target.value) }}
                            >
                              Buy
                            </button>
                          </li>
                          
                        </ul>
                      </div>
                    )
                  })}  
                </div>
              </main>
            </div>
          </div>

        );
    }
}

export default Main;
