import React, { Component } from 'react';
import logo2 from '../logo2.png';
//id=account를 app.js에서 정의함.(현재 계정주소 저장)
//현재계정에서 토큰주소에 이더보내면 토큰 받도록 해보기...

class Token extends Component{
    render(){
      return(
        <div className="container-fluid mt-5">
        <div className="row">
          <main role="main" className="col-lg-12 d-flex text-center"> 
            <div className="content mr-auto ml-auto">
              <img src={logo2} className="App-logo2" alt='mypic' />

              
              <h2>전력거래에 EVT 토큰을 사용하세요!</h2>
              <br></br>
              <h3>STEP1.</h3>
              <h4>계정에 EVT 토큰을 추가하세요.</h4>
              <h5>(EVT 토큰 : <b>0x9B38695B198B51E7257F47cb0044D572379bfaa9</b>) </h5>
              <h3>STEP2.</h3>
              <h4>ETH를 EVT로 환전하세요.</h4>
              <h5> (1 EVT는  0.1 ETH 입니다.)</h5>
              <form onSubmit={(event) => {
                      event.preventDefault()
                      const tokenvalue = this.tokenmount.value
                      this.props.buytoken(tokenvalue)
                      }}>
                      <div className="form-group mr-sm-w">
                          <input id="tokenmount" ref={(input) => {this.tokenmount = input}}
                          type="text" className="form-control" placeholder="환전할 이더를 입력하세요." required />
                      </div>
                      <button type="submit" className="btn btn-primary btn-block">환전</button>
              </form>
                         
              <p>
                <div><span id="sendethstatus"></span></div>
              </p>
              <br></br>
              
            </div>
          </main>
        </div>
        </div>

  
      );
    }
  }
  
  export default Token;
