import React, { Component } from 'react';
import Web3 from 'web3';
import './App.css';
import EvContract from './abis/EvContract.json'
import Navbar from './Navbar';
import Identicon from 'identicon.js';
import Main from './Main';
import Token from './Token';




class App extends Component {

  async componentWillMount() {
    await this.loadWeb3()
    await this.loadBlockchainData()
  }

  

  async loadWeb3() {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else {
      window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
    }
  }

  async loadBlockchainData(){
    const web3 = window.web3
    const accounts = await web3.eth.getAccounts()
    console.log(accounts)
    this.setState({account: accounts[0]})
    //Network id
    const networkId = await web3.eth.net.getId()
    const networkData = EvContract.networks[networkId]
    
    if(networkData){
      const evContract = web3.eth.Contract(EvContract.abi, networkData.address)
      this.setState({ evContract })
      console.log(EvContract)
      const cntA = await evContract.methods.cntA().call()
      this.setState({ cntA })
      //load posts
      for(var i = 0; i < cntA; i++){
        const car = await evContract.methods.cars(i).call()
        this.setState({
          cars: [...this.state.cars, car]
        })
      }
      console.log({ cars: this.state.cars })
      this.setState({ loading: false})
    }else{
      window.alert('EvContract contract not deployed to detected network.')
    }
    //address
    
    //abi
  }

  setCar(carNum, name, socI, socR){
    this.setState({ loading: true })
    this.state.evContract.methods.setCar(carNum, name, socI, socR).send({ from : this.state.account })
    .once('receipt', (receipt) => {
      this.setState({ loading: false })
    })
  }

  buycar(id, price){
    this.setState({ loading: true })
    this.state.evContract.methods.buycar(id).send({ from: this.state.account, value: price })
    .once('receipt', (receipt) => {
      this.setState({ loading: false })
    })
    console.log(price)
    console.log(id)
  }

  buytoken(){
    let amount = parseFloat(document.getElementById("ethamount").value);
    let toAddress = document.getElementById("toaddress").value;
    web3.eth.sendTransaction({
      from:account, to:toAddress, value:web3.toWei(amount, "ether")}
    );
  }
  

  constructor(props) {
    super(props)
    this.state = {
      account: '',
      evContract: null,
      cars: [],
      loading: true
    }

    this.setCar = this.setCar.bind(this)
    this.buycar = this.buycar.bind(this)

  }

  render() {
    return (
      <div>
        <Navbar account={this.state.account} />
        <Token account={this.state.account}
               sendether = {this.sendether} />
        <div className="container-fluid mt-5">
            <div className="row">
              <main role="main" className="col-lg-12 d-flex text-center"> 
                <div className="content mr-auto ml-auto">
                   
                </div>
              </main>
            </div>
        </div>

        { this.state.loading
          ? <div id="loader" className="text-center mt-5"><p>Loading...</p></div>
          :
            <Main 
                  cars={this.state.cars}
                  setCar={this.setCar}
                  buycar={this.buycar}
                  />
                  
            
        }
      </div>
    );
  }
}

export default App;
