import React, { Component } from "react";
import logo2 from "../logo2.png";
import "./App";
//id=account를 app.js에서 정의함.(현재 계정주소 저장)
//현재계정에서 토큰주소에 이더보내면 토큰 받도록 해보기...

class Token extends Component {
  render() {
    return (
      <div className="box1">
        {web3.eth.getBalance("0x1549A5087910ED97646d15ef3530705F9A85e442")}
        이것저것 {this.props.account}
        <div className="box1__column">
          <img src={logo2} className="img__token" alt="EVtoken" />
        </div>
        <div className="box1__column">
          <div className="box__title">전력거래에 EVT 토큰을 사용하세요!</div>
          <div className="box1__text">
            <h3>STEP1.</h3>
            <h5>
              SharEV와 함께하는 토큰 이코노미에 참여하기 위해, 자신의 계정에 EVT
              토큰을 추가하세요.
            </h5>
            <h6>토큰 주소 : {this.props.tokenAddress}</h6>
            <h3>STEP2.</h3>
            <h5>
              보유하고있는 Ethereum을 원하는 만큼 EVT 토큰으로 환전하세요. 1
              ETH는 10 EVT입니다.
            </h5>
          </div>

          <form
            className="form1"
            onSubmit={event => {
              event.preventDefault();
              const tokenvalue = this.tokenmount.value;
              this.props.buyToken(tokenvalue);
            }}
          >
            <div className="form1">
              <input
                id="tokenmount"
                ref={input => {
                  this.tokenmount = input;
                }}
                type="text"
                className="form-size"
                placeholder="환전할 이더를 입력하세요."
                required
              />
              <button type="submit" className="token__btn">
                환전
              </button>
            </div>
          </form>
        </div>
      </div>
    );
  }
}

export default Token;
