import React, { Component } from "react";
import logo2 from "../logo2.png";
import "./App";
//id=account를 app.js에서 정의함.(현재 계정주소 저장)
//현재계정에서 토큰주소에 이더보내면 토큰 받도록 해보기...

class Token extends Component {
  render() {
    return (
      <div className="box">
        <div className="box__column">
          <img src={logo2} className="img__token" alt="EVtoken" />
        </div>
        <div className="box__column">
          <h2 className="box__title">전력거래에 EVT 토큰을 사용하세요!</h2>
          <div className="box__text">
            <h3>STEP1.</h3>
            <h5>토큰 이코노미에 참여하기위해 계정에 EVT 토큰을 추가하세요.</h5>
            <h3>STEP2.</h3>
            <h5>ETH를 EVT로 환전하세요. (1 EVT는 0.1 ETH 입니다.)</h5>
          </div>

          <form
            onSubmit={event => {
              event.preventDefault();
              const tokenvalue = this.tokenmount.value;
              this.props.buyToken(tokenvalue);
            }}
          >
            <div className="form">
              <input
                id="tokenmount"
                ref={input => {
                  this.tokenmount = input;
                }}
                type="text"
                className="form-size"
                placeholder="환전할 이더를 입력하세요."
                required
              />
            </div>
            <button type="submit" className="button">
              환전
            </button>
          </form>
        </div>
      </div>
    );
  }
}

export default Token;
